mapper.select:
		SELECT appriver_no
        	, id
        	, apprival_order
        	, apprival_status
        	, apprival_dt
        	, rejected_reason
        	, edms_doc_no
        	, apprival_line_no
		FROM approver


				appriver_no = #{favoriteNo}


				id = #{favoriteName}


				apprival_order = #{apprivalOrder}


				edms_doc_no = #{edmsDocNo}


				apprival_line_no = #{apprivalLineNo}


		ORDER BY apprival_dt
