mapper.select:
SELECT approval_line_no , id , approval_line_name , create_dt , recent_used_dt , referrer , favorite_chk FROM approval_line
approval_line_no = #{approvalLineNo}
AND id = #{id}
AND approval_line_name = #{approvalLineName}
ORDER BY create_dt

mapper.insert:

	SELECT NVL(MAX(approval_line_no), 0) + 1
	FROM approval_line

INSERT INTO approval_line (
	  approval_line_no
	, id
	, approval_line_name
	, create_dt
	, recent_used_dt

		, referrer


		, favorite_chk

)
VALUES (
	  #{approvalLineNo}
	, #{id}
	, #{approvalLineName}
	, #{createDt}
	, #{recentUsedDt}

		, #{referrer}


		, #{favoriteChk}

)


	SELECT NVL(MAX(approver_no), 0) + 1
	FROM approver

INSERT INTO approver (
	  approver_no
	, approver
	, approval_order
	, approval_status
	, approval_dt
	, rejected_reason
	, edms_doc_no
	, approval_line_no
)
VALUES (
	  #{approverNo}
	, #{approver}
	, #{approvalOrder}
	, #{approvalStatus}
	, #{approvalDt}
	, #{rejectedReason}
	, #{edmsDocNo}
	, #{approvalLineNo}
)

mapper.select:
SELECT favorite_no , favorite_name , create_dt , update_dt , approval_line_no , id FROM approval_line_favorite
favorite_no = #{favoriteNo}
AND favorite_name = #{favoriteName}
ORDER BY create_dt

mapper.insert:

	SELECT NVL(MAX(favorite_no), 0) + 1
	FROM approval_line_favorite

INSERT INTO approval_line_favorite (
	  favorite_no
	, favorite_name
	, approval_line_no
	, id
	, create_dt
	, update_dt
)
VALUES (
	  #{favoriteNo}
	, #{favoriteName}
	, #{approvalLineNo}
	, #{id}
	, #{createDt}
	, #{updateDt}
)
