mapper.insert:


	SELECT NVL(MAX(complain_no), 0) + 1
	FROM complains




INSERT INTO complains(complain_no
					 ,complain_dt
					 ,complain_title
					 ,complain_content
					 ,complain_request
					 ,upload_dt
					 ,progress
					 ,complain_type
					 ,customer_name
					 ,phone
					 ,company_no)
VALUES				 (#{complainNo}
					 ,#{complainDt}
					 ,#{complainTitle}
					 ,#{complainContent}
					 ,#{complainRequest}
					 ,sysdate
					 ,0
					 ,#{complainType}
					 ,#{customerName}
					 ,#{phone}
					 ,#{companyNo})
