mapper.select:
SELECT edms_doc_no , id , title , content , file_name , submit_dt , approval_dt , approval_status , edms_form_no , share_status , share_folder_no FROM edms_doc
edms_doc_no = #{edmsDocNo}
AND id = #{id}
AND edms_form_no = #{edmsFormNo}
AND share_status = #{shareStatus}
AND share_folder_no = #{shareFolderNo}
ORDER BY submit_dt

mapper.insert:

    SELECT NVL(MAX(edms_doc_no), 0) + 1 FROM edms_doc

INSERT INTO edms_doc (
