)

mapper.update:
UPDATE edms_doc
SET 
      approval_status = #{approvalStatus}
    , current_approver_id = #{currentApproverId}
    , reject_reason = #{rejectReason}
    , approval_dt = SYSDATE
WHERE edms_doc_no = #{edmsDocNo}

UPDATE edms_doc
