mapper.select:
SELECT COUNT(*)
FROM folder
WHERE FILE_ROOMS_NO = #{folderVO.fileRoomsNo}

mapper.insert:

	SELECT NVL(MAX(FILE_NO), 1) + 1
	FROM files

INSERT INTO files
( FILE_NO
,FILE_NAME
,EXTENSION
,PATH
,FILE_SIZE
,UPLOAD_DT
,ID
,FOLDER_NO
,FILE_ROOMS_NO
,ATTATCH
)
VALUES
( #{fileNo}
,#{fileName}
,#{extension}
,#{path}
,#{fileSize}
,SYSDATE
,#{id}
,#{folderNo}
,#{fileRoomsNo}
,#{attatch}
)
