mapper.insert:
insert into attendances
values (
    attendances_seq.NEXTVAL
    ,#{attendancesCode}
    ,#{departmentNo}
    ,SYSDATE


	    	,(select  ROUND((SYSDATE - time) * 24 * 60)
			    FROM    attendances
			    WHERE attendances_no = 
			            (   SELECT max(attendances_no) 
			                FROM attendances
			                WHERE id = '#{id}')
			    )


	    	,null


    ,#{id}
)
