mapper.insert:

	SELECT NVL(MAX(leave_no), 0) + 1
	FROM leave           

INSERT INTO leave(
				  leave_no
				  , id
				  , leave_start_dt
				  , leave_end_dt
				  , leave_days
				  , leave_reason
				  , leave_type
				  )
VALUES (				
		#{leaveNo}
		, #{id}
		, TO_DATE(#{leaveStartDt}, 'YYYY-MM-DD"T"HH24:MI')
		, TO_DATE(#{leaveEndDt}, 'YYYY-MM-DD"T"HH24:MI')
		, #{leaveDays}
		, #{leaveReason}
		, #{leaveType}
		)
